#include <bits/stdc++.h>
using namespace std;

bool check(int x1,int y1,int x2,int y2,int x3,int y3,int x4,int y4)
{
    float a1 = sqrt(pow(x1 - x2,2) + pow(y1 - y2,2));
    float a2 = sqrt(pow(x2 - x3,2) + pow(y2 - y3,2));
    float a3 = sqrt(pow(x3 - x4,2) + pow(y3 - y4,2));
    float a4 = sqrt(pow(x4 - x1,2) + pow(y4 - y1,2));

    if (a1 == a2 && a2 == a3 && a3 == a4 && a4 == a1)
    {
        if ((y3-y1)*(y4-y2)==-(x3-x1)*(x4-x2))
        {
            return true;
        }
        else
        {
            return false;
        }
    }
    return false;
}

int main()
{

    int n;
    cin >> n;
    while (n--)
    {
        int x1, y1, x2, y2;
        int x3, y3, x4, y4;

        cin >> x1 >> x2 >> x3 >> x4;
        cin >> y1 >> y2 >> y3 >> y4;

        if (check(x1,y1,x2,y2,x3,y3,x4,y4)) cout << "YES\n";
        else cout << "NO\n";

    }
    return 0;
}
