#include <bits/stdc++.h>
using namespace std;

bool check(string a, string b) {
    for (int i = 0; i < a.size(); i++) {
        if (a[i] > b[i]) return 1;
    }
    return 0;
}

int main()
{
    string s, c;
    cin >> s;

    c = s;
    int n;
    cin >> n;

    while (n > 0 && next_permutation(s.begin(), s.end()))
    {
        if (check(s, c)) n--;
    }
    if (n > 0) cout << -1 << endl;
    else cout << s << endl;
}
