def Fx(a, n, k):
 r = 1
 for i in range(n):
  if i % 2==0:
   for j in range(k+n//2-1, -1, -1):
    a[i][j] = r
    r += 1
  else:
   for j in range(k+n//2):
    a[i][j] = r
    r += 1
 for i in range(n-1, -1, -1):
  if i % 2:
   for j in range(n//2+k, n):
    a[i][j] = r
    r += 1
  else:
   for j in range(n-1, k+n//2-1,-1):
    a[i][j] = r
    r += 1
 return a
