#include<bits/stdc++.h>
#define fr(i, n) for(int i = 0; i < n; i += 1)
#define ll long long

const int MAX = 3e+3;
const int M = 431;

bool prime[MAX];
int p[M], pp[M];
int mp_size;

void SavePrime()
{
    prime[0] = prime[1] = true;
    for (int i = 2; i*i <= MAX; i++) {
        if (!prime[i]) {
            for (int j = i * i; j <= MAX; j += i) {
                prime[j] = true;
            }
        }
    }
}
using namespace std;

int main()
{
    SavePrime();
    fr (i, 3000) {
        if (!prime[i]) {
            p[mp_size] = i;
            pp[mp_size++] = 0;
        }
    }
    ll n, m, q;
    cin >> n >> m >> q;
    ll a[n], b[m];
    fr (i, n) cin >> a[i];
    fr (i, m) cin >> b[i];
    sort(a, a + n);
    sort(b, b + m);
    int nx = n, mx = m;
    int k = 2;
    fr (i, n) {
        for (int x = k; x <= a[i]; x += 1) {
            int y = x;
            int cur = 0;
            while (y > 1) {
                int count = 0;
                while (y % p[cur] == 0) {
                    y /= p[cur];
                    count++;
                }
                pp[cur++] += count * nx;
            }
        }
        k = a[i] + 1;
        nx--;
    }
    k = 2;
    fr (i, m) {
        for (int x = k; x <= b[i]; x += 1) {
            int y = x;
            int cur = 0;
            while (y > 1) {
                int count = 0;
                while (y % p[cur] == 0) {
                    y /= p[cur];
                    count++;
                }
                pp[cur++] -= count * mx;
            }
        }
        k = b[i] + 1;
        mx--;
    }
    int t;
    fr (i, q) {
        cin >> t;
        int cur = 0;
        int mn = 1e+9;
        while (t > 1) {
            int count = 0;
            int key = 0;
            while (t % p[cur] == 0) {
                t /= p[cur];
                count++;
                key = 1;
            }
            if (key) mn = min(mn, int(pp[cur] / count));
            cur++;
        }
        cout << mn << endl;
    }

    return 0;
}
