#include <iostream>

using namespace std;

int main()
{
    ios_base::sync_with_stdio(false);
cin.tie(NULL);
cout.tie(NULL);

    int n;
    cin >> n;
    int a[n], l[n]={0}, r[n]={0};
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }
    
    for (int i = 0; i < n-1; i++) {
        if (a[i] <= a[i+1]) l[i+1] = l[i] + 1;
    }
    for (int i = n-1; i >= 1; i--) {
        if (a[i] <= a[i-1]) r[i-1] = r[i] + 1;
    }
    
    int mx = -10000000;
    for (int i = 0; i < n; i++) {
        mx = max(mx, l[i] + r[i] + 1);
    }
    
    cout << mx;
    

    return 0;
}
