#include <bits/stdc++.h>
using namespace std;
#define mx 1000001
long long a[mx], x;
int main() {
    ios_base::sync_with_stdio(0); cin.tie(NULL); cout.tie(NULL);
    int n, r;
    cin >> n >> r;
    for (int i = 1; i <= n; i++) {
        cin >> x;
        a[max(1, i-r)] += x;
        if (i+r+1 <= n) a[i+r+1] -= x;
    }
    for (int i = 1; i <= n; i++) {
        a[i] += a[i - 1];
        cout << a[i] << ' ';
    }

    return 0;
}
