#include<bits/stdc++.h>
using namespace std;
#define int long long

struct point {
    int x, y;
};

#define mx 500000
int a[mx], u;
point b[mx];

signed main() {

    ios_base::sync_with_stdio(0);
    cin.tie(NULL);
    cout.tie(NULL);

    int n, r, h;
    cin >> n >> r >> h;

    for (int i = 0; i < n; i++) {
        cin >> u;
        b[i].y = u;
        a[max(0ll, i - r)] += u;
        if (r + i + 1 < n) a[r+i+1] -= u;
    }

    b[0].x = a[0];

    for (int i = 1; i < n; i++) {
        a[i] += a[i-1];
        b[i].x = a[i];
    }

    sort(b, b + n, [](point u, point v){
         if (u.x == v.x) return u.y > v.y;
         return u.x < v.x;
         });
    bool key = false;
    for (int i = 0; i < n; i++) {
        if (b[i].x > h) {
            key = true;
            break;
        }
        h += b[i].y;
    }

    if (key) cout << "Game over";
    else cout << "Next level " << h;


   return 0;
}
