#include <bits/stdc++.h>
using namespace std;
#define mx 3001
int n, dp[mx][mx], p[mx*mx], x = -1, y = -1, l;
char c;
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);

    cin >> n;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            cin >> c;
            dp[i][j] = c - 48;
            if (c == '0') {
                x = i, y = j, l++;
            }
        }
    }
    if (x == -1 || l < 2) return cout << "No", 0;
    p[n*x+y] = 1;
    queue < vector < int > > q;

    q.push({x, y});
    while (!q.empty())
    {
        x = q.front()[0];
        y = q.front()[1];
        dp[x][y] = 1;
        q.pop();
        for (int i = -1; i <= 1; i++) {
            for (int j = -1; j <= 1; j++) {
                if (i*i + j*j == 1 && x+i>-1 && x+i<n && y+j>-1 && y+j<n && dp[x+i][y+j]==0) {
                    if (p[n*(x+i)+(y+j)] > 1) return cout << "No", 0;
                    if (p[n*(x+i)+(y+j)] == 0) {
                        p[n*(x+i)+(y+j)] += p[n*x+y]+1;
                        q.push({x+i, y+j});
                    }
                }
            }
        }
    }


    cout << "Yes";

    return 0;
}
