#include <bits/stdc++.h>
using namespace std;
int dp[2001][2001];
signed main() {

    ios_base::sync_with_stdio(false);
    cout.tie(NULL);
    int n;
    cin >> n;

    dp[0][0] = 1;
    dp[2*n-2][2*n-2] = 1;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (dp[i][j] > 0) {
                if (i+1 < n && dp[i+1][j] == 0) {
                    dp[i+1][j] += dp[i][j];
                }
                if (j+1 < n && dp[i][j+1] == 0) {
                    dp[i][j+1] += dp[i][j];
                }
                if (i+1<n && j+1<n && dp[i+1][j+1] == 0) {
                    dp[i+1][j+1] += dp[i][j]+1;
                }
            }
            dp[2*n-2-i][2*n-2-j] = dp[i][j];
            dp[2*n-2-i][j] = dp[i][j];
            dp[i][2*n-2-j] = dp[i][j];
        }
    }



    for (int i = 0; i < 2*n-1; i++) {
        for (int j = 0; j < 2*n-1; j++) {
            cout << dp[i][j] << ' ';
        }
        cout << endl;
    }

	return 0;
}
