#include <bits/stdc++.h>
using namespace std;
int x, y, u, v;
int f(int n) {
    int cnt = 0;
    while (n >= 5) {
        n /= 5;
        cnt += n;
    }
    return cnt;
}
int dfs(int n) {
    int l = 0, r = 1e9;
    while (l+1 < r)
    {
        int m = (l + r) / 2;
        if (f(m) > n) r = m;
        else l = m;
    }
    if (f(r) == n) return r+1;
    return r;
}
signed main() {

    cout << "+ 1" << endl;
    cin >> x;

    cout << "+ 2" << endl;
    cin >> y;
    if (x < y) {
        cout << "+ 4" << endl;
        cin >> u;
        if (y < u) {
            cout << "! " << dfs(x) - 2 << endl;
        }
        else {
            cout << "! " << dfs(x) - 3 << endl;
        }
    }
    else {
        cout << "+ 1" << endl;
        cin >> u;
        cout << "+ 1" << endl;
        cin >> v;
        if (y < u) {
            cout << "! " << dfs(y)-4 << endl;
        }
        else {
            if (u < v) {
                cout << "! " << dfs(u)-5 << endl;
            }
            else {
                cout << "! " << dfs(v)-6 << endl;
            }
        }
    }


	return 0;
}
