#include <bits/stdc++.h>
using namespace std;

int dfs(int n) {
    int s(0);
    while (n > 0) {
        s += n % 10;
        n /= 10;
    }
    return s;
}

signed main() {

    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);
    cout << "- 1" << endl;
    int n;
    cin >> n;
    while (n > 9) n = dfs(n);
    n++;
    while (n > 9) n = dfs(n);
    cout << "! " << n << endl;


	return 0;
}
