#include <iostream>
int main(){

    int T, x, y;
    scanf("%d", &T);
    while (T){
        int n, k;
        scanf("%d%d", &n, &k);
        if (k == 0)
            for (int i = 1; i <= n; i += 1)
                printf("%d ",i);
        else
        if (n % k == 0 && (n / k) % 2 == 0)
            for(int i = 1; i <= n; i += 2*k){
                for(int ii = i+k; ii <= i + 2 * k - 1; ii += 1)
                    printf("%d ",ii);
                for(int jj = i; jj <= i+k-1; jj += 1)
                    printf("%d ",jj);
                }
        else printf("%d",-1);
        printf("\n"); T-=1;
    }
}
