#include<bits/stdc++.h>
using namespace std;

void swap(vector<int> &arr,
    int i, int j)
{
  int temp = arr[i];
  arr[i] = arr[j];
  arr[j] = temp;
}
int solve(vector<int>arr,
      int N)
{
  int ans = 0;
  vector<int>temp = arr;
  
  map <int, int> h;
  
  sort(temp.begin(), temp.end());
  for (int i = 0; i < N; i++)
  {
    h[arr[i]] = i;
  }
  for (int i = 0; i < N; i++)
  {
    if (arr[i] != temp[i])
    {
    ans++;
    int init = arr[i];
    swap(arr, i, h[temp[i]]);
    h[init] = h[temp[i]];
    h[temp[i]] = i;
    }
  }
  return ans;
}
int main()
{
  int n;
  cin >> n;
  vector<int>mp;
  for(int i=0;i<n;i++){
    int e;
    cin >> e;
    mp.push_back(e);
  }
  
  int k = solve(mp,n);
  reverse(mp.begin(),mp.end());
  int k1 = solve(mp,n);
  cout << min(k,k1);
  return 0;
}
