#include <bits/stdc++.h>
#define int long long
using namespace std;

int solve(int arr[], int n, int x) {
    int sum(0);
    for (int i = 0; i < n; i++) {
        if (arr[i] > x) {
            sum += arr[i]-x;
        }
    }
    return sum;
}

signed main()
{
    int n, m;
    cin >> n >> m;
    
    int arr[n];
    for (int i = 0; i < n; i++) {
        cin >> arr[i];
    }
    
    int l = 0, r = *max_element(arr, arr + n);
    while (l+1 < r) {
        int x = (l + r) / 2;
        if (solve(arr, n, x) < m) r = x;
        else l = x;
    }
    
    cout << l;

    return 0;
}
