#include <bits/stdc++.h>

using namespace std;

bool check(int a[],int b[],int n)
{
    for (int i = 0; i < n; i++)
    {
        if (a[i] != b[i]) return false;
    }
    return true;
}

int main()
{
    int n;

    cin >> n;

    int a[n], b[n];

    for (int i = 0; i < n; i++)
    {
        cin >> a[i];
        b[i] = a[i];
    }

    sort(a,a+n);

    int x = -1, y = -1;

    for (int i = 0; i < n; i++)
    {
        if (a[i] != b[i])
        {
            x = i; break;
        }
    }

    for (int i = n - 1; i >= 0; i--)
    {
        if (a[i] != b[i])
        {
            y = i; break;
        }
    }

    swap(b[x],b[y]);

    if (check(a, b, n))
    {
        return cout << "yes\nswap " << ++x << " " << ++y, 0;
    }

    swap(b[x],b[y]);

    reverse(b+x,b+(y+1));

    if (check(a, b, n))
    {
        return cout << "yes\nreverse " << ++x << " " << ++y, 0;
    }

    return cout << "no", 0;

}
