#include<bits/stdc++.h>
using namespace std;
#define LL signed long long int
LL dp[300000+5][22];
LL sdp[300000+5];
int main(){
    int N = 300000;
    for(int i=0;i<=9;i+=1)dp[i][0] = 1;
    for(int i=0;i<=N;i+=1){
        for(int j=1;j<=20;j+=1){
            for(int d=0;d<=9;d+=1){
                if((i-d*(1<<j)) >= 0)
                    dp[i][j] = (dp[i][j] + dp[i-d*(1<<j)][j-1]);
            }
        }
        sdp[i] = sdp[i-1] + dp[i][20];
    }
    int Q;
    cin>>Q;
    while(Q){
        Q-=1;
        LL x;
        scanf("%lld",&x);
        //cin>>x;
        if(x==1){cout<<0<<endl;continue;}
        int L = 0;
        int R = N;
        while(L!=R){
            int m = ceil((L+R)/2.0);
            if(sdp[m] < x)L=m;
            else R = m-1;
        }
        LL val = L+1;
        x -= sdp[L];
        int pos = 0;
        for(int j=20;j>=1;j-=1){
            for(int d = 0;d<=9;d+=1){
                if(x > dp[val-d*(1<<j)][j-1]){
                    //cout<<j<<' '<<d<<endl;
                    x -= dp[val-d*(1<<j)][j-1];
                }
                else{
                    if(pos or d){
                        pos = 1;
                        cout<<d;
                        val -= d * (1<<j);
                    }
                    break;
                }
            }
        }
        printf("%lld\n",val);
        //cout<<val<<endl;
    }
}
