#include<bits/stdc++.h>
#define int long long
using namespace std;

int xorPairCount(int arr[], int n, int x)
{
	int result = 0;

	unordered_map<int, int> m;

	for (int i=0; i<n ; i++)
	{
		int curr_xor = x^arr[i];

		if (m.find(curr_xor) != m.end())
			result += m[curr_xor];

		m[arr[i]]++;
	}

	return result;
}

int32_t main()
{
    int N, K;
    cin >> N >> K;

    int arr[N];

    for (int i = 0; i < N; i++){
        cin >> arr[i];
    }
	cout << xorPairCount(arr, N, K);
	return 0;
}
