#include<bits/stdc++.h>

using namespace std;

const int M = 100001;
int mat1[M][26]={0};
int mat2[M][26]={0};

int main()

{

	ios_base::sync_with_stdio(false);    cin.tie(NULL);
	string s1;
	string s2;
	cin >> s1 >> s2;
	int n = s1.length();
	int m = s2.length();
	int ans[26]={0};

	for (int i = 1; i <= n; i++) {
		ans[s1[i-1]-97]++;
		for (int j = 0; j < 26; j++) {
			mat1[i][j] = ans[j];
		}

	}

	for (int i = 0; i< 26; i++) {
		ans[i] = 0;
	}



	for (int i = 1; i <= m; i++) {
		ans[s2[i-1]-97]++;
		for (int j = 0; j < 26; j++) {
			mat2[i][j] = ans[j];
		}

	}

	int t;
	cin >> t;

	while (t--) {

		int x, y, a, b;
		cin >> x >> y >> a >> b;
		if (x-y != a-b) cout << "NO\n";
		else {

			int k1, k2, key=1;
			for (int i = 0; i < 26; i++) {
				k1 = mat1[y][i] - mat1[x-1][i];
				k2 = mat2[b][i] - mat2[a-1][i];
				if (k1 != k2) {
					key = 0;
					break;
				}

			}

			if (key) cout << "YES\n";
			else cout << "NO\n";
		}
	}
	return 0;

}
