#include <bits/stdc++.h>
using namespace std;
int main()
{
  int n;
  cin >> n;
  int list[n];
  
  for (int i = 0; i < n; i++){
    cin >> list[i];
  }
  
  sort(list,list+n);
  
  if (n == 2) return cout << list[0],0;
  if (n % 2) cout << list[int(n/2)];
  else{
    int sum1 = 0;
    int sum2 = 0;
    int x = n / 2;
    int y = x + 1;
    
    for (int i = 0; i < x; i++){
      sum1 += abs(list[i] - list[x]);
    }
    for (int i = x; i < n; i++){
      sum1 += abs(list[i] - list[x]);
    }
    
    for (int i = 0; i < y; i++){
      sum2 += abs(list[i] - list[y]);
    }
    for (int i = y; i < n; i++){
      sum2 += abs(list[i] - list[y]);
    }
    if (sum1 > sum2){
      cout << list[y-1];
    }
    else{
      cout << list[x-1];
    }
  }
}
