#include <bits/stdc++.h>
#define INF 100001
#define int long long
using namespace std;
list < int > adj[INF];
bool P[INF];
int m = 1, xx = 0;
void DFStil(int v)
{
    P[v] = true;
    for (auto u : adj[v]) {
        if (!P[u]) {
            m = m + 1;
            xx = xx + adj[u].size();
            DFStil(u);
        }
    }
}

void solve() {

    int n, k;
    scanf("%lld%lld", &n, &k);
    int x, y;
    for (int i = 0; i < k; i++) {
        scanf("%lld%lld", &x, &y);
        adj[x].push_back(y);
        adj[y].push_back(x);
    }
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        m = 1;
        xx = adj[i].size();
        if (!P[i]) {
            DFStil(i);
            xx /= 2;
            ans += (m * (m - 1) / 2 - xx);
        }
    }
    cout << ans << endl;
}
int32_t main(){
    int t = 1;
    while (t--) solve();
    return 0;
}
