#include<bits/stdc++.h>
#define int long long
using namespace std;
int isprime(int n)
{
  for (int i = 2; i*i <= n; i++)
  	if (n % i == 0) return i;
  return n;
}
int solve(int a,int k){
  
  int p = 1;
  int b = 1;
  if (k == 1) return 1;
  if (a > k) return 0;
  while (k != 1)
  {
    int i = isprime(k);
    while (k % i == 0){
      k /= i;
      p *= i;
    }
    if (a % p != 0){
      b *= p;
    }
    p = 1;
  }
  return b;
}

int32_t main()
{
	
	int a, k;
	cin >> a >> k;
	
	cout << solve(a, k);
	
	return 0;
}
