#include <bits/stdc++.h>
#define ll long long int
#define pb push_back
#define se second
#define fi first
#define mk make_pair
#define REP(i,n) for(int i = 0; i < n; i++)
using namespace std;

int n,k;
ll s = 0,c = 0;
int a[100005];
unordered_map<int,ll> m = {{0,1}};

int main() {
    cin >> n >> k;
    
    REP(i,n){
        cin >> a[i];
        s += a[i];
        s %= k;
        
        c += m[s];
        if(m.count(s)){
            m[s] += 1;
        }else m[s] = 0;
        
    }
    
    
    
    cout << c;
}
