#include <bits/stdc++.h>
#define ll long long
#define REP(i,n) for(int i = 0; i < n; i++)

using namespace std;

int c=0,n,a[10][10];
int xT,yT;

void cs(int x, int y, int s){
    if(x == xT && y == yT){
        if(s == n*n) c++;
        return;
    }
    
    if(x+1 < n && a[x+1][y] != 1) {
        a[x+1][y] = 1;
        cs(x+1,y,s+1);
        a[x+1][y] = 0;
    }
    
    if(x-1 >= 0 && a[x-1][y] != 1) {
        a[x-1][y] = 1;
        cs(x-1,y,s+1);
        a[x-1][y] = 0;
    }
    
    if(y+1 < n && a[x][y+1] != 1) {
        a[x][y+1] = 1;
        cs(x,y+1,s+1);
        a[x][y+1] = 0;
    }
    
    if(y-1 >= 0 && a[x][y-1] != 1) {
        a[x][y-1] = 1;
        cs(x,y-1,s+1);
        a[x][y-1] = 0;
    }
}
int main() {
    int x,y;
    cin >> n;
    cin >> x >> y >> xT >> yT;
    x-=1;
    y-=1;
    xT-=1;
    yT-=1;
    
    REP(i,n)REP(j,n) a[i][j] = 0;
    
    a[x][y] = 1;
    cs(x,y,1);
    cout << c;
}
