#include<bits/stdc++.h>

using namespace std;
int e[99999];
#define int long long
int n,sum=0,q,k;
vector<int>a;
void max(int k,int i){
  if(i==n){
    if(k==0){
      cout<<sum-q;
      exit(0);
    }
    return ;
  }
  if(k>=a[i])
  max(k-a[i],i+1);
  max(k,i+1);
}
signed main(){
  cin>>n>>k;
  int min=0;
  a.resize(n);
  for(auto &x:a) cin>>x,sum+=x;
  vector<int> b=a;
  sort(b.begin(),b.end());
  for(int i=0;i<n-k;i++)min+=b[i];
  cout<<min<<" ";
  for(;;k++){
    q=k;
    max(k,0);
  }
}
