#include<bits/stdc++.h>
using namespace std;
int solve(int B, int n, int m) {
	int a[n], b[m];
	for (int i=0;i<n;i++)
	scanf("%d",&a[i]);
	for (int i=0;i<m;i++)
	scanf("%d",&b[i]);
	int ans = -1;
	for (int i=0;i<n;i++) {
		for (int j=0;j<m;j++) {
			if (a[i]+b[j]<=B) {
				ans = max(a[i]+b[j],ans);
			}
		}
	}
	return ans;
}
int main()
{
	int t = 1;
	while (t--) {
		int B, n, m;
		scanf("%d%d%d",&B,&n,&m);
		printf("%d\n",solve(B,n,m));
	}
	return 0;
}
