#include <bits/stdc++.h>

using namespace std;

void solve(int arr[], int n){
  int a, b, c;
  
  c = arr[--n];
  b = arr[--n];
  n = n - 1;
  
  for (int i = n; i > -1; i--)
  {
    a = arr[i];
    if(a+b>c && a+c>b && b+c>a)
    {
      cout << a << " ";
      cout << b << " ";
      cout << c << " ";
      return;
    }
    else
    {
      c = b; b = a;
    }
  }
  cout << -1;
}

int main()
{
  int n;
  cin >> n;
  
  int arr[n];
  for (int i = 0; i < n; i++){
    cin >> arr[i];
  }
  
  sort(arr, arr + n);
  
  int t = 1;
  while (t--) solve(arr, n);
  
  return 0;
}
