#include <bits/stdc++.h>
#define int unsigned long long int
using namespace std;

int fo(int n)
{
  // 2^p <= n < 2^p+1
  // return p+1
  int k = 1, p = 0;
  
  while (k <= n){
    k *= 2; p++;
  }
  return p;
}

int32_t main()
{
  int n;
  cin >> n;
  cout << fo(n) << endl;
  return 0;
}
