#include <bits/stdc++.h>
#define int long long
using namespace std;

int solve(int x, int y) {
  int ans = 0;
  while (x < y)
  {
    if (y % 2) {
      ans += 1;
      y += 1;
    }
    else {
      ans += 1;
      y /= 2;
    }
  }
  
  return ans + (x - y);
}

int32_t main()
{
    int t = 1;
    while (t--) {
      int x, y;
      cin >> x >> y;
      cout << solve(x, y) << endl;
    }
    return 0;
}
