#include <bits/stdc++.h>

using namespace std;

int main()
{
  string s;
  cin >> s;
  sort(s.begin(),s.end());
  int f=0;
  do
  {
    int n=s.size();
    int g=0;
    for(int i=0; i<n-1; i++)
    {
      if(s[i]==s[i+1])
      {
        g+=1;
      }
    }
    if(g==0)
    {
      f+=1;  
    }
  }
  while(next_permutation(s.begin(),s.end()));
  cout << f << endl;
}
