from math import *

def solve(listt, x):

	for i in listt:
		if x % i != 0:
			return False;
	return True;

def gsd(b, size):
	x = b[0]
	for y in range(1, size):
		x = gcd(x, b[y]);
	return x;

n, m = map(int,input().split())

a = list(map(int,input().split()))
b = list(map(int,input().split()))

k, c, ans = gsd(b, m), [], 0

for i in range(k):
	if k % (i + 1) == 0:
		c.append(i+1)

for i in c:
	if solve(a, i):
		ans = ans + 1;

print(ans)
