#include<bits/stdc++.h>
using namespace std;

string solve() {
  int n, m, x = 0;
  cin >> n;
  for (int i = 0; i < n; i++) {
    cin >> m;
    x = x ^ (i * (m % 2));
  }
  if (x == 0) return "Laziz";
  return "Adiz";
  
}

int main()
{
    int t;
    cin >> t;
    while (t--) cout << solve() << endl;
    return 0;
}
