#include<bits/stdc++.h>
using namespace std;
int a[5],sum[30000];
int main()
{
    cin>>a[0]>>a[1]>>a[2]>>a[3];
    sort(a,a+4);
    memset(sum,0,sizeof(sum)); int s=0;
    for (int i=a[1]+1; i<=a[2]; i++)
    {
        for (int j=i; j<=a[3]; j++)
        {
            s++; sum[i^j]++;
        }
    }
    long long ans=0;
    for (int i=a[1]; i>=1; i--)
    {
        for (int j=i; j<=a[3]; j++)
        {
            s++; sum[i^j]++;
        }
        for (int j=1; j<=min(a[0],i); j++)
        {
            ans+=s-sum[i^j];
        }
    }
    cout<<ans;
    return 0;
}
