#include <bits/stdc++.h>
using namespace std;

string solve()
{
    int n, k;
    cin >> n >> k;
    string ws;
    cin >> ws;

    int arr[n];
    int m = n / 2;
    for (int i = 0; i < m; i += 1) {
        if (ws[i] > ws[n-i-1]) {
            arr[i] = 0;
            arr[n-i-1] = 1;
            k -= 1;
        }
        else
        if (ws[i] < ws[n-i-1]) {
            arr[i] = 1;
            arr[n-i-1] = 0;
            k -= 1;
        }
        else {
            arr[i] = arr[n-i-1] = 0;
        }
    }
    if (n % 2 == 1) {
        arr[m] = 0;
    }
    int j = 0;
    if (k < 0) return "-1";
    string sl = "", sr = "";
    while (k > 0 && m > 0)
    {
        if (arr[j] == arr[n-j-1]) {
            if (k - 2 >= 0 && ws[j] != '9') {
                arr[j] = arr[n-j-1] = 1;
                k -= 2;
            }
        }
        else {
            if (arr[j] == 1 && ws[n-j-1] != '9') {
                arr[n-j-1] = 1;
                k -= 1;
            }
            else
            if (arr[n-j-1] == 1 && ws[j] != '9') {
                arr[j] = 1;
                k -= 1;
            }

        }
        m -= 1;
        j += 1;
    }

    if (n % 2 == 1) {
        if (k > 0) {
            arr[int(n / 2)] = 1;
            k -= 1;
        }
    }

    for (int i = 0; i < n/2; i += 1) {
        if (arr[i] == 1 && arr[n-i-1] == 1) {
            sl += "9";
            sr = "9" + sr;
        }
        else {
            if (arr[i] == 1 && arr[n-i-1] == 0) {
                sl += ws[n-i-1];
                sr = ws[n-i-1] + sr;
            }
            else {
                sl += ws[i];
                sr = ws[i] + sr;
            }
        }
    }
    if (n % 2 == 1) {
        if (arr[int(n/2)] == 1) {
            sl += "9";
        }
        else {
            sl += ws[int(n/2)];
        }
    }

    return sl + sr;

}

int main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
	int t = 1;
	while (t--) {
        cout << solve() << endl;
	}
	return 0;
}
