#include<bits/stdc++.h>
using namespace std;

int main()
{
    int x1, y1, z1;
    cin >> x1 >> y1 >> z1;
    int x2, y2, z2;
    cin >> x2 >> y2 >> z2;

    long long ans;

    if (z1 < z2) {
        ans = 0;
    }
    else {
        if (z1 == z2) {
            if (y1 < y2) {
                ans = 0;
            }
            else {
                if (y1 == y2) {
                    if (x1 <= x2) {
                        ans = 0;
                    }
                    else {
                        ans = 15 * (x1 - x2);
                    }
                }
                else {
                    ans = 500 * (y1 - y2);
                }
            }
        }
        else {
            ans = 10000 * (z1 - z2);
        }
    }
    cout << ans;

    return 0;
}
