#include <bits/stdc++.h>
using namespace std;

int solve(int n,int p){
  
	int x, y;
  
  	x = int(p / 2);
  
  	if (n % 2) y = int((n - p) / 2);
  	else y = int((n - p + 1) / 2);
  
  	return min(x, y);
}

int main()
{
  	int t = 1, n, p;
  
  	while (t--){
      
    	cin >> n >> p;
      	cout << solve(n, p) << endl;
      
    }
  
  return 0;
}
