#include <bits/stdc++.h>
#define int long long

using namespace std;

int solve(int n,int a,int b){
    if (a > b) swap(a, b);
    int ans = (n - 1) * a;
    int ss;

    for (int i = 1; i <= n; i++){
        if (i == 1){
            cout << ans << " ";
            ss = ans;
        }
        else{
            if (ans != ss){
                cout << ans << " ";
                ss = ans;
            }
        }
        ans = ans - a + b;
    }
}

int32_t main()
{
    int t;
    cin >> t;

    while (t--){
        int n, a, b;
        cin >> n >> a >> b;

        solve(n, a, b);

        cout << endl;
    }

    return 0;
}
