def solve():

	n = int(input())
	arr = list(map(int,input().split()))

	t1 = [arr]
	count = 0

	while t1:
		
		t2 = []
		key = False
		for x in t1:

			arr = x
			n = len(x)
			if len(set(arr)) == 1:
				if arr[0] == 0 and n > 1:
					key = True
					t2.append(arr[:n-1])
				elif n % 2 == 0:
					key = True
					t2.append(arr[:n//2])
				continue
			s = 0
			sm = sum(arr)
			for i in range(n):
				s += arr[i]
				if 2 * s >= sm:
					if 2 * s == sm:
						key = True
						t2.append(arr[:i+1])
						t2.append(arr[i+1:])
					break
		if key == True:
			count += 1
		t1 = t2
		if len(t1) == 1:
			if len(set(t1[0])) == 1 and t1[0][0] == 0:
				count += len(t1[0])
				return count - 1
	return count



test = int(input())
for i in range(test):
	print(solve())
