#include <bits/stdc++.h>
#define int long long

using namespace std;

int solve(int list[],int n){
    if (n == 0) return 0;
    return list[n-1] + solve(list, n - 1);
}

int32_t main()
{
    int t = 1;

    while (t--){
        int n;
        cin >> n;

        int list[n];
        for (int i = 0; i < n; i++){
            cin >> list[i];
        }

        cout << abs(solve(list, n)) << endl;
    }

    return 0;
}
