#include <bits/stdc++.h>
#define ll long long int
#define pb push_back
#define mk make_pair
#define se second
#define fi first
#define lllllllllllllllll long long
#define llllll long long
#define lll long long
using namespace std; 
int main() {
    vector<ll> g;
    g.pb(0);
    g.pb(1);
    g.pb(3);
    g.pb(5);
    for(int i=4;i<500000;i++){
        auto t=lower_bound(g.begin(), g.end(), i)-g.begin();
        g.pb(g[i-1]+t);
    }
    ll s=0;
    for(int i=1;i<g.size();i++){
        s+=g[i]-g[i-1];
    }
    
    ll n;
    cin>>n;  
    int t=lower_bound(g.begin(),g.end(),n)-g.begin(); 
    s=0;
    for(int i=1;i<t;i++){
        s+=(g[i]-g[i-1])*i;
    }   
    s+=(n-g[t-1])*t; 
    cout<<s;
}
