#include<bits/stdc++.h>
#define int long long
using namespace std;

int32_t main()
{
    int n;
    cin >> n;

    int mat[2*n][2*n];
    for (int i = 0; i < 2*n; i++){
        for (int j = 0; j < 2*n; j++){
            cin >> mat[i][j];
        }
    }
    int sum = 0;

    int R = 2 * n, C = 2 * n;

    for (int i = 0; i < R / 2; i++){
        for (int j = 0; j < C / 2; j++){
            int r1 = i;
            int r2 = R - i - 1;
            int c1 = j;
            int c2 = C - j - 1;

            sum += max(max(mat[r1][c1], mat[r1][c2]),max(mat[r2][c1], mat[r2][c2]));
        }
    }

    cout << sum << endl;

return 0;
}
