#include <bits/stdc++.h>
using namespace std;

int solve(int list[],int n,int k){
    int max = list[0];
    for (int i = 1; i < n; i++){
        if (max < list[i]){
            max = list[i];
        }
    }

    if (max < k) return 0;
    return max - k;
}

int main()
{
    int t = 1;

    while (t--){
        int n, k;
        cin >> n >> k;

        int list[n];
        for (int i = 0; i < n; i++){
            cin >> list[i];
        }

        cout << solve(list, n, k) << endl;
    }

    return 0;
}
