def solve(word):
	ans, k = 0, 0
	for x in word:
		if 'a' <= x and x <= 'z':
			k = k + 1
	if k == 0:
		ans += 1
	k = 0
	for x in word:
		if 'A' <= x and x <= 'Z':
			k = k + 1
	if k == 0:
		ans += 1
	k = 0
	for x in word:
		if x.isnumeric() == True:
			k = k + 1
	if k == 0:
		ans += 1
	k = 0
	for x in word:
		if ('a' > x or x > 'z') and ('A' > x or x > 'Z') and ('0' > x or x > '9'):
			k = k + 1
	if k == 0:
		ans += 1
	if len(word) + ans >= 6:
