#include <bits/stdc++.h>
#define int long long
using namespace std;  

int partition(int arr[],int brr[],int x,int y)  
{
    int pivot = arr[y];
    int i = (x - 1);

    for(int j = x; j <= y-1; j++)  
    {
        if (arr[j] < pivot)  
        {
        i++;
            swap(arr[i], arr[j]);
            swap(brr[i], brr[j]);
        }
    }
    swap(arr[i + 1], arr[y]);
    swap(brr[i + 1], brr[y]);

    return (i + 1);  
}

void quickSort(int arr[],int brr[],int x,int y)
{
    if (x < y)  
    {
       int pi = partition(arr, brr, x, y);
       quickSort(arr, brr, x, pi - 1);
       quickSort(arr, brr, pi + 1, y);
    }  
}

int32_t main()
{
    int t = 1;
    
    while (t--)
    {
      int n;
      
      cin >> n;
      
      int arr1[n], arr2[n];
      
      for (int i = 0; i < n; i++)
      {
        cin >> arr1[i];
        arr2[i] = i;
      }
      
      quickSort(arr1,arr2,0,n-1);
      
      int min = 10000000000000000;
      
      for (int i = 0; i < n - 1; i++)
      {
        if (arr2[i] >= arr2[i+1] && arr1[i+1] - arr1[i] < min)
        {
          min = arr1[i+1] - arr1[i];
        }
      }
      
      cout << min << endl;
    }
    
    return 0;  
}
