#include<bits/stdc++.h>
using namespace std;

int countTriplets (int arr[], int d, int n)
{
	int count = -1;

	map<int, int> set;

	for(int i = 0; i < n; i++)
	{
		if ((set.find(arr[i] - d) != set.end()) &&
			(set.find(arr[i] - 2 * d) != set.end()))
		{
			count += (set[arr[i] - d] *
					set[arr[i] - 2 * d]);
		}

		if (set.find(arr[i]) == set.end())
			set[arr[i]] = 1;
		else
			set[arr[i]] += 1;
	}
	return count;
}

int main()
{
	int n, d;
	cin >> n >> d;

	int arr[n];
	for (int i = 0; i < n; i++)
        cin >> arr[i];

    cout << countTriplets(arr, d, n) + 1;

	return 0;
}
