#include <bits/stdc++.h>

#define int long long

int solve(int n)
{
    int sum = 0;
    while (n != 0)
    {
        sum += (n % 10);
        n /= 10;
    }
    return sum;
}

using namespace std;
int32_t main()
{
    int n, k = 0;
    cin >> n;
    int arr[1000];

    for (int i = 1; i <= n / i; i++){
        if (n % i == 0){
            arr[k++] = i;
            arr[k++] = (n / i);
        }
    }

    sort(arr, arr + k);

    int maxx = solve(arr[0]);
    int maxy = arr[0];
    for (int i = 1; i < k; i++){
        if (maxx < solve(arr[i])){
            maxx = solve(arr[i]);
            maxy = arr[i];
        }
        else{
            if (maxx == solve(arr[i])){
                if (maxy > arr[i]){
                    maxy = arr[i];
                }
            }
        }
    }
    cout << maxy ;

    return 0;
}
