def funcBefore(k,a):
    return (a-1)*(2 - k%2)*a // 2 + a*a*( (k-1)//2 )

t = int(input())
for i in range(t):
    n,k = map(int,input().split())
    a = n // k
    r = n % k
    
    res = funcBefore(k,a)
    # print(res,end=" ")
    if r != 0: 
        res += a*min( (k-1)//2 , r) + (a+1)*max(0,r - (k-1)//2 - (k-1)%2)
        if k % 2 == 0 and r >= k//2: res += a
    print(res)
