#include <bits/stdc++.h>
using namespace std;
int f(int arr[], int n, int mn)
{
	int count = 0;

	for (int i = 0; i < n; i++) {
		int val = arr[i];
		if (arr[i] > mn) {
			arr[i] = arr[i] - mn;
			count += arr[i] / 5;
			arr[i] = arr[i] % 5;
			count += arr[i] / 2;
			arr[i] = arr[i] % 2;
			if (arr[i]) {
				count++;
			}
		}
		arr[i] = val;
	}
	return count;
}

int solve(int arr[], int n)
{
	sort(arr, arr + n, greater<int>());
	int mn = arr[n - 1];

	int c1 = 0, c2 = 0, c3 = 0;
	c1 = f(arr, n, mn);
	c2 = f(arr, n, mn - 1);
	c3 = f(arr, n, mn - 2);
	return min(c1, min(c2, c3));
}

int main()
{
	int t;
	scanf("%d",&t);
	while (t--)
    {
        int n;
        scanf("%d",&n);
        int arr[n];
        for (int i = 0; i < n; i++) {
            scanf("%d",&arr[i]);
        }
        printf("%d\n",solve(arr, n));
    }
}
