#include <bits/stdc++.h>
#define int long long
using namespace std;

int sumXOR(int arr[], int n)
{
	int sum = 0;
	for (int i = 0; i < 32; i++)
	{
		int zc = 0, oc = 0;

		int idsum = 0;
		for (int j = 0; j < n; j++)
		{
			if (arr[j] % 2 == 0)
				zc++;
			else
				oc++;
			arr[j] /= 2;
		}

		idsum = oc * zc * (1 << i);

		sum += idsum;
	}
	return sum;
}

int32_t main()
{
	int n, sum = 0;
	cin >> n;

	int arr[n];

	for (int i = 0; i < n; i++){
        cin >> arr[i];
	}

	sum = sumXOR(arr, n);
	cout << sum;
	return 0;
}
