#include <bits/stdc++.h>
using namespace std;

string largestString(string num, int k)
{
	string ans = "";
	for (auto i : num) {
		while (ans.length() && ans.back() < i && k > 0) {
			ans.pop_back();
			k--;
		}
		ans.push_back(i);
	}

	while (ans.length() and k--) {
		ans.pop_back();
	}

	return ans;
}

int main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

	string str;
	int k;
	cin >> str >> k;

	cout << largestString(str, k) << endl;
}
