#include<bits/stdc++.h>
using namespace std;
const int M = 26;
const int N = 1000001;
int mat[N][M];
bool check(int i, int left, int k)
{
    for (int j = 0; j < M; j++) {
        if (mat[i][j]-mat[left][j] > k) {
            return false;
        }
    }
    return true;
}
int solve()
{
    int n, k;
    scanf("%d%d",&n,&k);
    string s;
    cin >> s;
    string s2 = "";
    for (int i = 0; i < n; i++) {
        if ('a' <= s[i] && s[i] <= 'z') {
            s2 += s[i];
        }
        else {
            s2 += char(s[i]+32);
        }
    }
    s = s2;
    for (int i = 1; i <= n; i++) {
        for (int j = 0; j < 26; j++) {
            mat[i][j] = mat[i-1][j];
        }
        mat[i][s[i-1]-97]++;
    }
    int mx = -1;
    int ans = 0;
    int left = 0;   //7 1
                    //Hasanov
    for (int i = 1; i <= n; i++) {
        if (check(i, left, k)) {
            ans++;
        }
        else {
            mx = max(mx, ans++);
            while (left < n)
            {
                ans--;
                left++;
                if (check(i, left, k)) {
                    break;
                }
            }
        }
    }
    return max(mx, ans);
}

int main()
{
    int t = 1;
    //scanf("%d",t);
    while (t--) {
        printf("%d\n",solve());
    }

    return 0;
}
